import React, { useState } from "react";

export default function TextForm(props) {
  const handleUpClick = () => {
    console.log("Upppercase was clicked");
    let newText = text.toUpperCase();
    setText(newText);
  };
  const handleLoClick = () => {
    console.log("Lowercase was clicked");
    let newText = text.toLowerCase();
    setText(newText);
  };
  const [text, setText] = useState("");

  const copyText = (text) => {
    console.log("text", text);
    var textField = document.createElement("textarea");
    textField.innerText = text;
    document.body.appendChild(textField);
    textField.select();
    document.execCommand("copy");
    textField.remove();
  };

  const clearText = () => {
    console.log("Reset was clicked");
    let newText = "";
    setText(newText);
  };

  const handleOnChange = (event) => {
    console.log("Text was updated.");
    // takes input as sets it to text
    setText(event.target.value);
  };

  return (
    <div>
      <div className="container">
        <h2>{props.heading}</h2>
        <div className="mb-3">
          <textarea
            className="form-control"
            value={text}
            onChange={handleOnChange}
            id="myBox"
            rows="8"
          ></textarea>
        </div>
        <button className="btn btn-outline-primary" onClick={handleUpClick}>
          Convert To Uppercase
        </button>
        <button className="btn btn-outline-primary mx-2" onClick={handleLoClick}>
          Convert To LowerCase
        </button>
        <button className="btn btn-outline-success mx-2" onClick={copyText}>
          Copy to clipboard
        </button>
        <button className="btn btn-outline-danger mx-2" onClick={clearText}>
          Reset
        </button>
      </div>
      <div className="container my-3">
        <h3>Text Summary</h3>
        <p>
          {text.split(" ").length} words and {text.length} characters
        </p>
        <p>{text.split(" ").length * 0.08} min read</p>
        <h2>Preview</h2>
        <p>{text}</p>
      </div>
    </div>
  );
}
