import React, { useState } from "react";

export default function TextForm(props) {
  const handleUpClick = () => {
    console.log("Upppercase was clicked");
    let newText = text.toUpperCase();
    setText(newText);
  };
  const handleDownClick = () => {
    console.log("Lowercase was clicked");
    let newText = text.toLowerCase();
    setText(newText);
  };

  const handleOnChange = (event) => {
    console.log("Text was updated.");
    // takes input as sets it to text
    setText(event.target.value);
  };

  const [text, setText] = useState("Enter your text here to analyse");
  return (
    <div>
      <div className="container">
        <h2>{props.heading}</h2>
        <div className="mb-3">
          <textarea
            className="form-control"
            value={text}
            onChange={handleOnChange}
            id="myBox"
            rows="8"
          ></textarea>
        </div>
        <button className="btn btn-outbtn-primary" onClick={handleUpClick}>
          Convert To Uppercase
        </button>
        <button className="btn btn-outbtn-primary mx-2" onClick={handleDownClick}>
          Convert To LowerCase
        </button>
      </div>
      <div className="container my-3">
        <h3>Text Summary</h3>
        <p>
          {text.split(" ").length} words and {text.length} characters
        </p>
        <p>{text.split(" ").length * 0.08} min read</p>
        <h2>Preview</h2>
        <p>{text}</p>
      </div>
    </div>
  );
}
